import React from 'react';
import PropTypes from 'prop-types';

import {
	Panel,
	PanelHeader,
	Header,
	Button,
	Group,
	Cell,
	Div,
	Avatar,
	FormItem,
	File,
	View,
	Progress
} from '@vkontakte/vkui';
import {Icon24Camera, Icon24Document} from "@vkontakte/icons";

function generatePresentation(){
	const { spawn } = require('child_process');

	const py = spawn('python', ['pdf_parsing.py']);
	let dataToSend = 'C:/Users/compf/Documents/Diploma/data/НИР_ВласенкоНА.pdf';

	py.stdout.on('data', function (data) {
		console.log('Python output:', data.toString());
	});

	py.stdin.write(JSON.stringify(dataToSend));
	py.stdin.end();
}
const Home = ({ id, go, fetchedUser }) => (
	<View activePanel="panel">
		<Panel id="panel">
			<PanelHeader>Easyntation</PanelHeader>
			<Group>
				<FormItem top="Загрузите документ">
					<File before={<Icon24Document role="presentation" />} size="l" mode="secondary" />
				</FormItem>
				<Div>
					<Button size="xl" level="2" onClick={generatePresentation}>
						Сгенерировать
					</Button>
				</Div>
				<FormItem>
					TODO:сделать так чтобы прибавлялось
					<Progress aria-labelledby="progresslabel" value={null} />
				</FormItem>
			</Group>
		</Panel>

	</View>
)
Home.propTypes = {
	id: PropTypes.string.isRequired,
	go: PropTypes.func.isRequired,
	fetchedUser: PropTypes.shape({
		photo_200: PropTypes.string,
		first_name: PropTypes.string,
		last_name: PropTypes.string,
		city: PropTypes.shape({
			title: PropTypes.string,
		}),
	}),
};

export default Home;
