import React from 'react';
import PropTypes from 'prop-types';

import {Panel, PanelHeader, Header, Button, Group, Cell, Div, Avatar, FormItem, File, View} from '@vkontakte/vkui';
import {Icon24Camera, Icon24Document} from "@vkontakte/icons";

const Home = ({ id, go, fetchedUser }) => (
	<View activePanel="panel">
		<Panel id="panel">
			<PanelHeader>Easyntation</PanelHeader>
			<Group>
				<FormItem top="Загрузите документ">
					<File before={<Icon24Document role="presentation" />} size="l" mode="secondary" />
				</FormItem>
				<Div>
					<Button
						onClick={() => {
							setIsButtonLoading(true);
							setTimeout(() => {
								setIsButtonLoading(false);
							}, 1000);
						}}
						isLoading={isButtonLoading}
					>
						Click me
					</Button>
				</Div>
			</Group>
		</Panel>
	</View>)
Home.propTypes = {
	id: PropTypes.string.isRequired,
	go: PropTypes.func.isRequired,
	fetchedUser: PropTypes.shape({
		photo_200: PropTypes.string,
		first_name: PropTypes.string,
		last_name: PropTypes.string,
		city: PropTypes.shape({
			title: PropTypes.string,
		}),
	}),
};

export default Home;
